package com.nishant.practice.forLoop;

import java.util.Scanner;

public class TableOfAnyNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		for (int i = 1; i <= 10; i++) {
			int a = i * 4;
			System.out.print(a + " ");
		}

	}

}
